rch strong, h3#comments, #respond h3');			Cufon.replace('#featured .info h2', {					 					textShadow: '-1px 1px black'				});										$('#featured .info h2').css({					'font-size' : 50, 					'lineHeight' : 70								})			//feature slider				$("#featured > ul").tabs({fx:{opacity: "toggle"}}).tabs("rotate", 5000, true);						$("#featured").hover(function(){				$("#featured > ul").tabs("rotate", 0, true);				}, function(){				$("#featured > ul").tabs("rotate", 5000, false);				}						)			//sidebars			$('.sidebar.left').stickyfloat({duration: 400});				$('.sidebar.right').stickyfloat({duration: 600});			//pretty photo			$("a[href$='.jpg'], a[href$='.jpeg'], a[href$='.gif'], a[href$='.png']").prettyPhoto({				animationSpeed: 'normal', /* fast/slow/normal */				padding: 40, /* padding for each side of the picture */				opacity: 0.35, /* Value betwee 0 and 1 */				showTitle: true, /* true/false */				allowresize: true, /* true/false */				counter_separator_label: '/', /* The separator for the gallery counter 1 "of" 2 */				theme: 'light_rounded', /* light_rounded / dark_rounded / light_square / dark_square */				callback: function(){}			});			//contact-form			$('form#contactForm').submit(function() {				$('form#contactForm .error').remove();				var hasError = false;				$('.requiredField').each(function() {					if(jQuery.trim($(this).val()) == '') {						var labelText = $(this).prev('label').text();						$(this).parent().append('<span class="error">Вы забыли ввести '+labelText+'.</span>');						hasError = true;					} else if($(this).hasClass('email')) {						var emailReg = /^([\w-\.]+@([\w-]+\.)+[\w-]{2,4})?$/;						if(!emailReg.test(jQuery.trim($(this).val()))) {							var labelText = $(this).prev('label').text();							$(this).parent().append('<span class="error">Вы ввели неверный '+labelText+'.</span>');							hasError = true;						}					}				});				if(!hasError) {					$('form#contactForm li.buttons button').fadeOut('normal', function() {						$(this).parent().append('<img src="/wp-content/themes/td-v3/images/template/loading.gif" alt="Loading&hellip;" height="31" width="31" />');					});					var formInput = $(this).serialize();					$.post($(this).attr('action'),formInput, function(data){						$('form#contactForm').slideUp("fast", function() {				   							$(this).before('<p class="thanks"><strong>Спасибо!</strong> Ваше сообщение было успешно отправлено. Вам вскоре ответят.</p>');						});					});				}						return false;							});					})	</script></head><body><div id="header-wrap"><div id="header"><h1 style="margin-top: 30px;"><a href="<?php bloginfo('url');?>"><?php bloginfo('name'); ?></a></h1><div id="access" role="navigation">			<?php wp_nav_menu( array( 'container_class' => 'menu-header', 'theme_location' => 'primary' ) ); ?></div><!-- #access --></div><!--header--></div><!--header-wrap--><div id="wrap"><div id="inner-wrap">